ruleContent.imageDecode:
var Magua = new JavaImporter();
Magua.importPackage(
    Packages.java.io,
    Packages.android.graphics
);

with(Magua) {
    // 1. 解码原始图片数据
    var img = BitmapFactory.decodeByteArray(result, 0, result.length);
    var originalWidth = img.getWidth();
    var originalHeight = img.getHeight();

    // 2. 判断是否需要旋转（仅当 width > height * 3 时旋转）
    if (originalWidth > originalHeight * 4.5) {
        // 3. 创建新图片（旋转后宽高互换）
        var newWidth = originalHeight;
        var newHeight = originalWidth;
        var newImg = Bitmap.createBitmap(newWidth, newHeight, Bitmap.Config.ARGB_8888);

        // 4. 使用 Matrix 旋转图片（顺时针90度）
        var canvas = new Canvas(newImg);
        var matrix = new Matrix();
        matrix.setRotate(90);
        matrix.postTranslate(newWidth, 0); // 调整位移避免裁剪

        // 5. 绘制旋转后的图片
        canvas.drawBitmap(img, matrix, null);

        // 6. 转换为字节数组输出
        var outputStream = new ByteArrayOutputStream();
        newImg.compress(Bitmap.CompressFormat.PNG, 100, outputStream);
        result = outputStream.toByteArray();
    }
    // 否则直接保留原图（无需操作）
}
