	const {java,source} = this;
	t = String(t).trim();
	if(/^\d+_[a-zA-Z\d]{32}$/.test(t)){
		source.putLoginHeader(t);
		java.longToast("✅登录成功");
		}else{
			java.toast("❌token输入有误，形如 230484_ahshxbh3oAjjdjxbjsnxxxxxx");

			}
	}

