function getCode(t) {
	try {
		let 验证码 = result.get("验证码（填完验证码后，要点👤登录）");
		let 账号 = result.get("账号");
		let 密码 = result.get("密码");
		if (!/^\d{4,}/.test(验证码)) {
			let res = JSON.parse(java.ajax(getloginUrl()));
			if (res.code == "221003") {
				let url = "https://app.jjwxc.org//appDevicesecurityAndroid/getDeviceSecurityCode";
				body = "versionCode=401&username=" + encodeURIComponent(账号) + "&checktype=" + t;
				option = {
					"method": "POST",
					"body": String(body)
				}
				msg = JSON.parse(java.ajax(url + "," + JSON.stringify(option))).data.message;
				java.toast(msg);
			} else {
				if (/\d+_[a-z\d]+/.test(res.token)) {
					java.log("获取到的token值: " + res.token);
					java.longToast("登录成功" + res.token);
					LoginToken(res.token)
				}
				msg = /校验验证码/.test(res.message) ? "请请尝试填入上次发给你的验证码（可能是很久之前的）" : res.message;
				msg = msg??"已成功登录"+res.token+"，无需填验证码";
			   java.log(msg);
			   java.toast(msg);
			}
		} else {
			let getCodeurl = getloginUrl() + "&checktype=" + t + "&checkdevicecode=" + 验证码;
			let data = java.ajax(getCodeurl);
			let token = JSON.parse(data).token ?? JSON.parse(data).message;
			java.longToast(token);
			java.log("获取到的token值: " + token)
			LoginToken(token)
		}
	} catch (e) {
		java.log(e)
	}
}

function LoginToken(token){
	try{
	token = String(token).replace(/undefined|token=|\s|&.*/g,'');
	token = /^\d+_\w{32}$/.test(token)?token:"";
	if(token==""){
		throw new Error("设置token失败，请检查填写的token值")
		}

	let fl = Map("fl")==""?"9,10":Map("fl");
	let bq = Map("bq")==""?"5":Map("fl");	
