  } else if (let (list = $.data_list) list != 0 && $.data_list != undefined)  {
    arr = $.data_list.map($ => $.book_id_str)
  } else {
    java.toast("è·å– book_id å¤±è´¥ï¼Œä½ å¯èƒ½éœ€è¦ç™»å½•ï¼")
  }
  return arr.slice(page*100, (page+1)*100)
}

