jsLib:
function getTocData(d){
	const {java,cache}=this
	let toc=java.get('toc')
	let data=cache.get(toc)
	if(data){
		if(d)cache.delete(toc)
		return JSON.parse(data)
}
	data=String(java.ajax(toc))
	if(data.trim()[0]!='{'){
		return 
	}
	let tocData=JSON.parse(data)
	tocData=tocData.oridata||tocData.data
	tocData=tocData.split('-//-')
	.map(v=>{v=v.split('-/-');v[3]=v[3]&&v[3].trim();return {i:v[0],cid:v[1],title:v[2],vip:v[3],pay:v[3]&&(v[3]=='unvip'||java.get('pay')=='')}})
 if(java.get('source')=='uukanshu')tocData.reverse()
 cache.put(toc,JSON.stringify(tocData),60)
 return tocData
}

