];

function getArguments(open_argument, key) {
  try {
    open_argument = JSON.parse(open_argument);
  } catch (e) {
    open_argument = {
      media: "小说",
      tone_id: "默认音色",
      server: host[0],
      source: "全部",
      source_type: "男频",
    };
  }
  if (key) {
    return open_argument[key];
  } else {
    return open_argument;
  }
}

function setArguments(key, value) {
	const { source } = this;
	let open_argument = source.getVariable();
	open_argument = getArguments(open_argument, '');
	open_argument[key] = value;
	open_argument = JSON.stringify(open_argument);
	source.setVariable(open_argument);
	return open_argument;
}

function decrypt(Text) {
  return Text;
}

function getComments(content, bid, cid) {
  let { java, cache, source } = this;
  try {
    let base_url = getArguments(source.getVariable(), "server");
    let fqssionid = getArguments(source.getVariable(), "fqssionid") || "";
    let apiUrl = `${base_url}/para_idea?item_id=${cid}&ssionid=${fqssionid}`;
    let comments = java.ajax(apiUrl);

    let lines = content.split("\n");
    let raw = JSON.parse(comments).data.data;

    Object.keys(raw).forEach((x) => {
      cache.putMemory(`fq-${bid}-${cid}-${x}-text`, lines[x]);
      let plcolor = getArguments(source.getVariable(), "plcolor");
      if (!plcolor) {
        plcolor = "#000000";
      }
      lines[x] += `<img src="${this.createSvg(
        raw[x].count,
        plcolor,
        bid,
        cid,
        x
      )}">`;
    });

    return lines.join("\n");
  } catch (e) {
    return content;
  }
}

function createSvg(number, color, bid, cid, para) {
  let displayText = number > 99 ? "99+" : number;
  let date = String(Date.now()).match(/(\d{6}$)/)[1];
  this.cache.putMemory(`fq-${bid}-${cid}-${para}`, date);
  let svg = `<svg width="1000" height="800" xmlns="http://www.w3.org/2000/svg">
        <rect x="25" y="25" width="950" height="750" rx="250" ry="250" fill="none" stroke="${color}" stroke-width="25"/>
        <text x="510" y="650" font-family="Arial, sans-serif" text-anchor="middle" dominant-baseline="middle" font-size="30" fill="${color}">刷新图片查看段评</text>
        <text x="510" y="565" font-family="Arial, sans-serif" text-anchor="middle" dominant-baseline="middle" font-size="425" fill="${color}">${displayText}</text>
    </svg>`;

  let encodedSvg = this.java.base64Encode(svg);
  return `data:image/svg+xml;base64,${encodedSvg},{"js":"showCmt('${bid}','${cid}','${para}','${date}')"}`;
}

function showCmt(bid, cid, para, date) {
  let { java, cache, source } = this;
  let mname = `fq-${bid}-${cid}-${para}`;
  let load = String(cache.getFromMemory(mname));
  let isqread=0;
  try {isqread = java.qread();} catch (e) {}
  if (load == date && !isqread) {
    cache.deleteMemory(mname);
    return;
  }
  cache.deleteMemory(mname);
  let base_url = getArguments(source.getVariable(), "server");
  let fqssionid = getArguments(source.getVariable(), "fqssionid") || "";;
  let apiUrl = `${base_url}/get_para_review?item_id=${cid}&book_id=${bid}&para=${para}&ssionid=${fqssionid}`;
  let title = cache.getFromMemory(mname + "-text") || "段评内容";
  if (
    !cache.getFromMemory("fq.bopenat") ||
    Date.now() - parseInt(cache.getFromMemory("fq.bopenat")) > 400
  ) {
    cache.putMemory("fq.bopenat", String(Date.now()));
    java.startBrowser(apiUrl, title);
  }
}

function cleanHTML(html) {
  let result = html
    .replace(/<header[^>]*>[\s\S]*?<\/header>/gi, "")
    .replace(/<div class="tt-title"[^>]*>[\s\S]*?<\/div>/gi, "")
    .replace(/<(?!\/?p\b|\/?img\b)[^>]+>/gi, "");
  result = result.replace(/<\/?p[^>]*>/g, "\n");
  return result.replace(/\n+/g, "\n").trim();
}
var javaImport = new JavaImporter();
javaImport.importClass(
  Packages.android.util.Base64,
  Packages.java.lang.String,
  Packages.java.net.URL,
  Packages.okhttp3.HttpUrl
);
with (javaImport) {
  function btoa(data) {
    return Base64.encodeToString(String(data).getBytes("UTF-8"), 2);
  }

  function getSubDomain(url) {
    let baseUrl = getBaseUrl(url);
    if (!baseUrl) {
      return url;
    }
    try {
      let mURL = URL(baseUrl);
      let host = mURL.host;
      if (isIPAddress(host)) return host;
      return HttpUrl.parse(baseUrl).topPrivateDomain() || host;
    } catch (e) {
      this.java.log(e);
      return baseUrl;
    }
  }

  function getDomain(url) {
    let baseUrl = getBaseUrl(url);
    if (!baseUrl) {
      return url;
    }
    try {
      return URL(baseUrl).host;
    } catch (e) {
      return baseUrl;
    }
  }
  /**
   * 移除cookie
   */
  function removeCookie(url) {
    const { cookie } = this;
    cookie.removeCookie(url);
    let domains = [getDomain(url), getSubDomain(url)];
    domains.forEach((domain) => {
      cookie.removeCookie(domain);
    });
  }
}

function getBaseUrl(url) {
  if (!url) {
    return null;
  }
  url = String(url);
  if (url.match(/https?:\/\//i)) {
    var index = url.indexOf("/", 9);
    return index == -1 ? url : url.substring(0, index);
  }
  return null;
}

function isIPv4Address(ip) {
  ip = String(ip);
  let parts = ip.split(".");
  if (parts.length !== 4) return false;

  for (let part of parts) {
    if (!/^\d+$/.test(part)) return false; // 必须是数字
    if (part.length > 1 && part[0] === "0") return false; // 禁止前导零
    let num = parseInt(part, 10);
    if (num < 0 || num > 255) return false; // 范围检查
  }
  return true;
}

function isIPv6Address(ip) {
  ip = String(ip);
  // 处理双冒号（最多出现一次）
  if (ip.includes(":::")) return false;
  let doubleColonCount = (ip.match(/::/g) || []).length;
  if (doubleColonCount > 1) return false;

  // 分割成组
  let groups = ip.split(":");
  let validGroupCount = 8;
  let actualGroupCount = groups.filter((g) => g !== "").length;

  // 验证组数
  if (doubleColonCount === 1) {
    if (actualGroupCount > validGroupCount - 1) return false;
  } else {
    if (groups.length !== validGroupCount) return false;
  }

  // 验证每组内容
  for (let group of groups) {
    if (group === "") continue; // 跳过空组（双冒号部分）
    if (!/^[0-9a-fA-F]{1,4}$/.test(group)) return false; // 1-4位十六进制
  }
  return true;
}

function isIPAddress(input) {
  return isIPv4Address(input) || isIPv6Address(input);
}

function getSessionId(cookieString) {
    const match = cookieString.match(/sessionid=([^;]+)/);
    return match ? match[1] : null;
}

function getKey(key) {
  let parts = key.split(";");
  for (let part of parts) {
    if (part.includes("qttoken")) {
      return part.split("=")[1];
    }
  }
  return "";
}

function getFanqieCookie() {
	   const { cookie } = this;
    try {
        return String(cookie.getCookie('fanqienovel.com') || java.getCookie('fanqienovel.com') || '');
    } catch (e) {
        return '';
    }
}

