	function isVips(res) {
    let isVIP = '';
    let vipEndTime = res.vip_end_time;
    let formattedDate = '';

    if (vipEndTime && vipEndTime !== 0) {
        let date = new Date(vipEndTime * 1000);
        formattedDate = `${date.getFullYear()}-${(date.getMonth() + 1).toString().padStart(2, '0')}-${date.getDate().toString().padStart(2, '0')}`;
        let currentTime = Math.floor(Date.now() / 1000);
        let remainingDays = Math.ceil((vipEndTime - currentTime) / (24 * 60 * 60));
        if (currentTime > vipEndTime) {
            isVIP = `${res.vip_level === 1 ? "VIP" : "SVIP"} (已过期)`;
        } else if (remainingDays <= 7) {
            isVIP = `${res.vip_level === 1 ? "VIP" : "SVIP"} 剩余${remainingDays}天`;
        } else {
            if (vipEndTime < 1912946812) {
                isVIP = `${res.vip_level === 1 ? "VIP" : "SVIP"}(${formattedDate})`;
            } else {
                isVIP = `${res.vip_level === 1 ? "VIP" : "SVIP"} (永久)`;
            }
        }
    } else {
        isVIP = '您尚未开通VIP';
    }
    return isVIP;
}

