// 保存登录UI信息
function putLoginInfo(info) {
	try {
		let key = java.androidId()
		let encodeStr = Packages.android.util.Base64.encodeToString(java.createSymmetricCrypto("AES", key).encrypt(info), 2)
		cache.put(`userInfo_${source.getKey()}`, encodeStr)
		return true
	} catch (e) {
		java.log(e)
		return source.putLoginInfo(info)
	}
}

