  (async function() {
    const loading = document.getElementById('loading');
    const container = document.getElementById('container');
    const currentVersion = document.getElementById('currentVersion');
    const latestVersion = document.getElementById('latestVersion');
    const statusAlert = document.getElementById('statusAlert');
    const buttonGroup = document.getElementById('buttonGroup');
    const versionItemLatest = document.getElementById('versionItemLatest');
    const versionBadgeContainer = document.getElementById('versionBadgeContainer');
    const latestLogContainer = document.getElementById('latestLogContainer');
    const latestLogDate = document.getElementById('latestLogDate');
    const latestLogContent = document.getElementById('latestLogContent');
    const logsContainer = document.getElementById('logs');
    const logList = document.getElementById('logList');

    const localVer = '${String(localVersion)}';

    // 优化版本比较 - 支持更复杂的版本号
    function compareVersions(vs) {
      const normalize = (v) => {
        return v.split('.').map(n => {
          const num = parseInt(n, 10);
          return isNaN(num) ? 0 : num;
        });
      };

      const parts1 = normalize(localVer);
      const parts2 = normalize(vs);
      const maxLength = Math.max(parts1.length, parts2.length);

      for (let i = 0; i < maxLength; i++) {
        const num1 = parts1[i] || 0;
        const num2 = parts2[i] || 0;
        if (num1 > num2) return 1;
        if (num1 < num2) return -1;
      }
      return 0;
    }

    // 获取版本号的视觉表示
    function getVersionBadge(current, latest) {
      const compare = compareVersions(latest);
      if (compare === -1) {
        const currentParts = current.split('.');
        const latestParts = latest.split('.');
        const majorDiff = parseInt(latestParts[0]) - parseInt(currentParts[0]);
        if (majorDiff > 0) {
          return '<div class="version-badge">主版本更新</div>';
        } else {
          return '<div class="version-badge minor">小版本更新</div>';
        }
      }
      return '';
    }

    const encodedEndpoints = [
      'aHR0cDovL3YxLmd5a3MuY2Y=',
      'aHR0cDovL3YyLmd5a3MuY2Y=',
      'aHR0cDovL3YzLmd5a3MuY2Y=',
      'aHR0cDovL3Y0Lmd5a3MuY2Y=',
      'aHR0cDovL3Y1Lmd5a3MuY2Y=',
      'aHR0cDovLzEwMS4zNS4xMzMuMzQ6ODg4OA=='
    ];

    function decodeEndpoint(str) {
      return atob(str);
    }

    async function fetchVersionData() {
      for (const b64 of encodedEndpoints) {
        const url = decodeEndpoint(b64);
        try {
          const response = await fetch(url + '/version', { timeout: 2000 });
          if (response.ok) {
            return await response.json();
          }
        } catch (e) {
          console.warn(\`接口失败：\${url}\`, e);
        }
      }
      throw new Error('所有更新接口都请求失败');
    }

    function showError(message) {
      loading.innerHTML = \`
        <div class="error-state">
          <div class="error-icon"><i class="fas fa-sad-tear"></i></div>
          <div class="error-text">\${message}</div>
          <button class="retry-button" onclick="location.reload()"><i class="fas fa-redo"></i> 重试</button>
        </div>
      \`;
    }

    try {
      const data = await fetchVersionData();
      const cloudVersion = String(data.version3);
      const updateLog = data.update_log || {};

      // 显示版本信息
      currentVersion.textContent = \`v\${localVer}\`;
      latestVersion.textContent = \`v\${cloudVersion}\`;
      versionBadgeContainer.innerHTML = getVersionBadge(localVer, cloudVersion);

      // 处理日志 - 分离最新和历史
      const logEntries = Object.entries(updateLog);
      if (logEntries.length > 0) {
        // 显示最新日志
        const [latestDate, latestContent] = logEntries[0];
        latestLogDate.innerHTML = \`<i class="fas fa-calendar-alt"></i> \${latestDate}\`;
        latestLogContent.textContent = latestContent;
        latestLogContainer.style.display = 'block';

        // 显示历史日志（除了最新的）
        if (logEntries.length > 1) {
          const historyLogs = logEntries.slice(1);
          logList.innerHTML = historyLogs.map(([date, content]) => \`
            <div class="log-item">
              <div class="log-item-date">
                <i class="fas fa-calendar-alt"></i>
                <span>\${date}</span>
              </div>
              <div class="log-item-content">\${content}</div>
            </div>
          \`).join('');
          logsContainer.style.display = 'block';
          // 默认折叠历史日志
          logList.classList.add('collapsed');
          document.getElementById('logsToggle').classList.add('collapsed');
          logsCollapsed = true;
        }
      }

      // 检查更新状态
      const compareResult = compareVersions(cloudVersion);
      if (compareResult === -1) {
        versionItemLatest.classList.remove('no-update');
        statusAlert.style.display = 'none';

        // 生成下载按钮
        const domainMap = {
          main: 'aHR0cDovL3N5LnFpbmd0aWFuNjE4LmNvbQ==',
          d1: 'aHR0cDovL3YxLmd5a3MuY2Y=',
          d2: 'aHR0cDovL3YyLmd5a3MuY2Y=',
          d3: 'aHR0cDovL3YzLmd5a3MuY2Y=',
          d4: 'aHR0cDovL3Y0Lmd5a3MuY2Y=',
          d5: 'aHR0cDovL3Y1Lmd5a3MuY2Y=',
          d6: 'aHR0cDovLzEwMS4zNS4xMzMuMzQ6ODg4OA=='
        };

        function decode(b64) {
          return atob(b64);
        }

        const path = '/sy/download/%E5%AE%89%E5%8D%93%E9%98%85%E8%AF%BBapp-%E6%99%B4%E5%A4%A9%E8%9E%8D%E5%90%884.0(vip%E5%AE%8C%E5%85%A8%E7%89%88).json';
        const mainPath = '/download/%E5%AE%89%E5%8D%93%E9%98%85%E8%AF%BBapp-%E6%99%B4%E5%A4%A9%E8%9E%8D%E5%90%884.0(vip%E5%AE%8C%E5%85%A8%E7%89%88).json';

        const routes = [
          { name: '主线路', icon: 'rocket', domain: 'main', suffix: mainPath },
