          { name: '备用线路2', icon: 'satellite', domain: 'd2', suffix: path },
          { name: '备用线路3', icon: 'link', domain: 'd3', suffix: path },
          { name: '备用线路4', icon: 'bolt', domain: 'd4', suffix: path },
