          return \`
            <a href="yuedu://booksource/importonline?src=\${encodeURIComponent(fullUrl)}" class="button">
              <i class="fas fa-\${r.icon}" style="font-size: 16px;"></i>
              <span>\${r.name}</span>
            </a>
          \`;
        }).join('');
        buttonGroup.style.display = 'flex';
      } else {
        // 已是最新版本
        statusAlert.className = 'status-alert up-to-date';
        statusAlert.innerHTML = '<i class="fas fa-check-circle"></i> <div>✅ 您已是最新版本</div>';
        statusAlert.style.display = 'flex';
      }

      // 显示主容器，隐藏加载
      loading.style.display = 'none';
      container.style.display = 'block';

    } catch (err) {
      console.error('版本检查失败：', err);
      showError('<i class="fas fa-exclamation-circle"></i> 检查更新失败，请稍后重试<br><small>' + err.message + '</small>');
    }
  })();
  </script>
</body>
