function getComments(content, bid, cid) {
  let { java, cache, source } = this;
  try {
    let base_url = getArguments(source.getVariable(), "server");
    let fqssionid = getArguments(source.getVariable(), "fqssionid") || "";
    let apiUrl = `${base_url}/para_idea?item_id=${cid}&ssionid=${fqssionid}`;
    let comments = java.ajax(apiUrl);

    let lines = content.split("\n");
    let raw = JSON.parse(comments).data.data;

    Object.keys(raw).forEach((x) => {
      cache.putMemory(`fq-${bid}-${cid}-${x}-text`, lines[x]);
      let plcolor = getArguments(source.getVariable(), "plcolor");
      if (!plcolor) {
        plcolor = "#000000";
      }
      lines[x] += `<img src="${this.createSvg(
        raw[x].count,
        plcolor,
        bid,
        cid,
        x
      )}">`;
    });

    return lines.join("\n");
  } catch (e) {
    return content;
  }
}

function createSvg(number, color, bid, cid, para) {
  let displayText = number > 99 ? "99+" : number;
  let date = String(Date.now()).match(/(\d{6}$)/)[1];
  this.cache.putMemory(`fq-${bid}-${cid}-${para}`, date);
  let svg = `<svg width="1000" height="800" xmlns="http://www.w3.org/2000/svg">
        <rect x="25" y="25" width="950" height="750" rx="250" ry="250" fill="none" stroke="${color}" stroke-width="25"/>
        <text x="510" y="650" font-family="Arial, sans-serif" text-anchor="middle" dominant-baseline="middle" font-size="30" fill="${color}">刷新图片查看段评</text>
        <text x="510" y="565" font-family="Arial, sans-serif" text-anchor="middle" dominant-baseline="middle" font-size="425" fill="${color}">${displayText}</text>
    </svg>`;

  let encodedSvg = this.java.base64Encode(svg);
  return `data:image/svg+xml;base64,${encodedSvg},{"js":"showCmt('${bid}','${cid}','${para}','${date}')"}`;
}

function showCmt(bid, cid, para, date) {
  let { java, cache, source } = this;
  let mname = `fq-${bid}-${cid}-${para}`;
  let load = String(cache.getFromMemory(mname));

  if (load == date) {
    cache.deleteMemory(mname);
    return;
  }
  cache.deleteMemory(mname);
  let base_url = getArguments(source.getVariable(), "server");
  let fqssionid = getArguments(source.getVariable(), "fqssionid") || "";;
  let apiUrl = `${base_url}/get_para_review?item_id=${cid}&book_id=${bid}&para=${para}&ssionid=${fqssionid}`;
  let title = cache.getFromMemory(mname + "-text") || "段评内容";
  if (
    !cache.getFromMemory("fq.bopenat") ||
    Date.now() - parseInt(cache.getFromMemory("fq.bopenat")) > 400
  ) {
    cache.putMemory("fq.bopenat", String(Date.now()));
    java.startBrowser(apiUrl, title);
  }
}

function cleanHTML(html) {
  let result = html
    .replace(/<header[^>]*>[\s\S]*?<\/header>/gi, "")
    .replace(/<div class="tt-title"[^>]*>[\s\S]*?<\/div>/gi, "")
    .replace(/<(?!\/?p\b|\/?img\b)[^>]+>/gi, "");
  result = result.replace(/<\/?p[^>]*>/g, "\n");
  return result.replace(/\n+/g, "\n").trim();
}
