function set_media(media) {
    const mediaConfig = {
        '喜马拉雅': ['听书'],
        '番茄': '*',
        '福利小说':['小说'],
        '如漫画':['漫画'],
        '包子漫画':['漫画'],
        '漫画网':['漫画'],
        '风车漫画':['漫画'],
        '福利漫画':['漫画'],
        '好看漫画':['漫画'],
        '六月听书':['听书'],
        '海洋听书':['听书'],
        '七猫': ['小说', '听书'],
        '河马': ['短剧'],
        '超会专属短剧': ['短剧'],
        '歪瑞古德': ['漫画'],
        '毒舌影视':['短剧'],
        '全部': '*', // 允许所有模式
        '默认': ['小说']
    };

	const source = getArgument('source');
	const allowedModes = mediaConfig[source] || mediaConfig['默认'];
	let targetMedia = mediaConfig['默认'][0]; 
	let isAllowed = false;

	if (allowedModes === '*' || allowedModes.includes(media)) {
		targetMedia = media;
		isAllowed = true;
	} else if (Array.isArray(allowedModes)) {
		targetMedia = allowedModes[0]; 
	}

	const message = isAllowed ?
		`\n\n已切换至：${targetMedia}\n请重新搜索书籍！` :
		`\n\n目前${source}：不支持【${media}】模式！\n已自动切换至：${targetMedia}`;

	setArgument('media', targetMedia);
	java.toast(message);
}

