}

function createCommentHtmlTag(displayText, url, sourceName) {
    const encodedUrl = url.replace(/&/g, '&amp;');
    return '<comment count="' + displayText + '" onPress="java.' + 
           (isQRead.call(this) ? 'startBrowserDp' : 'startBrowser') + 
