    let isIOS = !deviceType.call(this);
    if (isIOS) {
        let commentUrl = buildCommentUrl.call(this, sourceType, bid, cid, para);
        let sourceName = getCommentSourceName(sourceType);
        return createCommentHtmlTag.call(this, displayText, commentUrl, sourceName);
    }

    var svg;
    if (bubbleStyle == '1') {
        svg = '<svg width="1000" height="833" xmlns="http://www.w3.org/2000/svg">' +
