function checkClickRateLimit(lastCallKey) {
    let { cache } = this;
    let lastCallTime = cache.getFromMemory(lastCallKey) || 0;
    let currentTime = Date.now();

    if (currentTime - lastCallTime < 1000) {
        return false;
    }
    cache.putMemory(lastCallKey, currentTime);
    return true;
}

function handleCommentClick(sourceType, bid, cid, para, date, cacheKey) {
    let { cache } = this;
    let mname = cacheKey;
    let load = (cache.getFromMemory(mname) ?? "-").split("-");

    if (load[0] != "1" || load[1] != date) {
        cache.putMemory(mname, "1-" + date);
        return;
    }

    let lastCallKey = `last_call_${sourceType}_${bid}_${cid}_${para}`;
    if (!checkClickRateLimit.call(this, lastCallKey)) {
        return;
    }

    let commentUrl = buildCommentUrl.call(this, sourceType, bid, cid, para);
    let title = cache.getFromMemory(mname + "-text") ?? getCommentSourceName(sourceType) + "æ®µè¯„";

    startCommentBrowser.call(this, commentUrl, title);
}

function getComments(content, bid, cid, sourceType = 'fq', contentMd5 = null) {
    let { java, cache, source } = this;
    let host = getServerHost.call(this);
    try {
        let lines = content
            .replace(/(<img[^>]*?>)\n/g, "$1")
            .split("\n")
            .filter(line => line.trim());

        if (sourceType == 'qm') {
            if (!contentMd5) return wrapContentForIOS.call(this, content);

            let apiUrl = `${host}/proxy_qmidea?action=paragraph_bubbles&book_id=${bid}&item_id=${cid}&content_md5=${contentMd5}`;
            let response = java.ajax(apiUrl);
            let data = JSON.parse(response).data;

            if (data && data.chapters && data.chapters.length > 0) {
                let chapterData = data.chapters[0];
                if (chapterData.bubbles) {
                    chapterData.bubbles.forEach((bubble, bubbleIndex) => {
                        let paraMd5 = bubble.p;
                        let count = parseInt(bubble.c) || 0;

                        if (count > 0 && bubbleIndex < lines.length) {
                            let paraIndex = bubbleIndex;

                            cache.putMemory(`qm-${bid}-${cid}-${paraMd5}-text`, lines[paraIndex]);

                            let color = getCommentIconColor.call(this);
                            let commentTag = createSvg.call(this, count, color, bid, cid, paraMd5, sourceType);
                            if (deviceType.call(this)) {
                                lines[paraIndex] += `<img src="${commentTag}">`;
                            } else {
                                lines[paraIndex] += commentTag;
                            }
                        }
                    });
                }
            }
        } else {
            let apiUrl = `${host}/proxy_idea?item_id=${cid}`;
            let comments = java.ajax(apiUrl);
            let raw = JSON.parse(comments).data.data;

            Object.keys(raw).forEach((x) => {
                cache.putMemory(`fq-${bid}-${cid}-${x}-text`, lines[x]);

                let color = getCommentIconColor.call(this);
                let commentTag = createSvg.call(this, raw[x].count, color, bid, cid, x, sourceType);
                if (deviceType.call(this)) {
                    lines[x] += `<img src="${commentTag}">`;
                } else {
                    lines[x] += commentTag;
                }
            });
        }

        let result = lines.join("\n");
        return wrapContentForIOS.call(this, result);
    } catch (e) {
        return wrapContentForIOS.call(this, content);
    }
}

function showCmt(bid, cid, para, date) {
    handleCommentClick.call(this, 'fq', bid, cid, para, date, `fq-${bid}-${cid}-${para}`);
