function search(name, page) {
    let resp = getAjaxJson(urlIP(urlSearchArtwork(name, page)))
    java.log(urlIP(urlSearchArtwork(name, page)))
    if (resp.error === true || resp.total === 0) {
        return {"data": [], "total":0, "lastPage": 0}
    }
