    removeCacheList("likeNovels")
    removeCacheList("watchedSeries")
}

function removeSettingsCache() {
    // åˆ é™¤ è‡ªåŠ¨ç¿»é¡µçš„æœ€å¤§é¡µç 
    cache.delete("maxPagesKey")
    cache.delete("novelsMaxPages")
