@js:
const slidesArrayMatch = result.match(/var slides_p_path = \[\s*([^\]]+)\s*\];/s);
if (slidesArrayMatch) {
    const arrayContent = slidesArrayMatch[1];
    const base64Matches = arrayContent.match(/"([^"]+)"/g);
    if (base64Matches) {
        const decodedUrls = base64Matches.map(base64Str => {
            const cleanStr = base64Str.replace(/"/g, '');
            return java.base64Decode(cleanStr)
        });

        decodedUrls.map(x => '<img src="' + x + '" loading="lazy">').join("")
    }
}

