let hosts = [
"https://skybook.pages.dev",
"https://skybook.legado1.top",
"https://sw.skybook.qzz.io"
]

function bhost() {
  const { source, java } = this
  let host = source.getVariable()
  if (host == "") {
    host = checkLatency(hosts, this)[0].host
    source.setVariable(host)
  }
  return host
}

function checkLatency(urls, ctx) {
  const { java } = ctx
  var results = [],
    count = 0;
  for (var i = 0; i < urls.length; i++) {
    var url = urls[i].startsWith("http") ? urls[i] : "https://" + urls[i];
    try {
      java.toast(`æ£€æµ‹å¯ç”¨çº¿è·¯ï¼š(${i + 1}/${urls.length})`);
      var start = Date.now();
      org.jsoup.Jsoup.connect(url)
        .method(org.jsoup.Connection.Method.HEAD)
        .timeout(5000)
        .ignoreHttpErrors(true)
        .execute();
      var end = Date.now();
      var latency = end - start;
      if (latency > 0) count++;
      results.push({ host: url, latency: latency });
    } catch (e) {
      java.log(url + ":" + e);
    }
  }
  results.sort(function (a, b) {
    return a.latency - b.latency;
  });
  java.longToast(`æ£€æµ‹å®Œæˆï¼šå…±(${count}/${urls.length})ä¸ªå¯ç”¨çº¿è·¯`);
  return results;
}

