let item_list = [];

if (baseUrl.includes("activities")) {
  let res = java.ajax(bhost() + "/content.json");
  let posts = JSON.parse(res).posts || [];
  let sixHours = 6 * 60 * 60 * 1000;

  let activities = posts.map(function(post) {
    let title = post.title || 'æ— æ ‡é¢˜';
    let permalink = post.path || '#';
    let author = post.author || 'æ˜æœˆ';
    let date = post.date;
    let updated = post.updated;

    if (!date) return null;
    let publish = new Date(date);
    if (isNaN(publish)) return null;

    let effective = publish;
    let type = 'publish';

    if (updated) {
      let upd = new Date(updated);
      if (!isNaN(upd) && (upd - publish) > sixHours) {
        type = 'update';
        effective = upd;
      }
    }

    return { type, date: effective, title, permalink, author };
  }).filter(Boolean);

  let sorted = activities
    .filter(a => !isNaN(a.date))
    .sort((a, b) => b.date - a.date)
    .slice(0, 20);

  sorted.forEach(function(a) {
    let action = a.type === 'update' ? 'æ›´æ–°äº†æ–‡ç« ' : 'å‘å¸ƒäº†æ–‡ç« ';
    let d = a.date;
    let dateStr = `${d.getFullYear()}å¹´${d.getMonth() + 1}æœˆ${d.getDate()}æ—¥ ${d.getHours().toString().padStart(2, '0')}:${d.getMinutes().toString().padStart(2, '0')}`;
    let title = `${a.author} ${action} ${a.title}`;
    let url = bhost() + (a.permalink.startsWith('/') ? a.permalink : '/' + a.permalink);
    item_list.push({ title, url, date: dateStr });
  });

} else {
  let list = java.getElements(".post-item");
  list.forEach(function(item) {
    let doc = org.jsoup.Jsoup.parse(item.html ? item.html() : item);
    let title = doc.select(".title").text();
    let date = doc.select(".post-time").text();
    let url = doc.select("h2>a").attr("href");
    item_list.push({ title, url: bhost() + url, date });
  });
}

