  if (baseUrl.includes("activities")) return
  if (baseUrl.includes("page")) {
    page = baseUrl.match(/page\/(\d+)/)[1]
    return java.log(baseUrl.replace(/(\d+)\/$/, +page + 1 + "/"))
  } else {
    return baseUrl + "page/2/"
  }
})()

