        pageclass3 = $("#pageclass3").value.replace(/(\+|\?)/g, '\\$1').replace(/é¡µç /, '{\{page}}')
        h = "";
        if (html.match(/::/) && pageclass != "" && pageclass2 != "" && !/{\{page}}/.test(html)) {
            list = html.split(/\n|&&/);
            for (i in list) {
                if (list[i].match(/::/)) {
                    text = list[i].match(/(.*?)::/)[1];
                    href = list[i].match(/::(.*)/)[1]
                    if (href.match(reg)) {
