    function getInput() {
        var str = $("#input").value;
        var list = parseToDOM(str);
        if (list.length == 0) {
            $("#input").placeholder = "æ ¼å¼åŒ–å¤±è´¥ï¼ï¼ï¼è¯·æ£€æŸ¥æ˜¯å¦ä¸ºå¸¦htmlæ ‡ç­¾çš„æ–‡æœ¬"
        } else {
            var html = "";
            for (let i = 0; i < list.length; i++) {
