    function getInput3() {
        text = $("#input").value;
        if (text.match(/\&\&|::/g)) {
            text = text.replace(/\&\&/g, '\n');
            list = text.match(/.*::.*/g);
            html = [];
            for (i = 0; i < list.length; i++) {
                l = list[i].match(/(.*)::(.*)/);
                title = l[1];
                url = l[2]
                html.push({
