        } else {
            document.getElementById("textMatchResult").value = "åŒ¹é…ä½ç½®ï¼š" + regex.lastIndex + "\r\nåŒ¹é…ç»“æœï¼š" + result[0];
        }
        return true;
    }

    function onReplace() {
        var str = document.getElementById("textSour").value;
