function transitionJsonToString (jsonObj, callback) {
	// è½¬æ¢åçš„jsonObjå—ä½“å¯¹è±¡
	var _jsonObj = null;
	// åˆ¤æ–­ä¼ å…¥çš„jsonObjå¯¹è±¡æ˜¯ä¸æ˜¯å­—ç¬¦ä¸²ï¼Œå¦‚æœæ˜¯å­—ç¬¦ä¸²éœ€è¦å…ˆè½¬æ¢ä¸ºå¯¹è±¡ï¼Œå†è½¬æ¢ä¸ºå­—ç¬¦ä¸²ï¼Œè¿™æ ·åšæ˜¯ä¸ºäº†ä¿è¯è½¬æ¢åçš„å­—ç¬¦ä¸²ä¸ºåŒå¼•å·
	if (Object.prototype.toString.call(jsonObj) !== "[object String]") {
		try {
			_jsonObj = JSON.stringify(jsonObj);
		} catch (error) {
			// è½¬æ¢å¤±è´¥é”™è¯¯ä¿¡æ¯
			alert('æ‚¨ä¼ é€’çš„jsonæ•°æ®æ ¼å¼æœ‰è¯¯ï¼Œè¯·æ ¸å¯¹...');
			alert(error);
			callback(error);
		}
	} else {
		try {
			jsonObj = jsonObj.replace(/(\')/g, '\"');
			_jsonObj = JSON.stringify(JSON.parse(jsonObj));
		} catch (error) {
			// è½¬æ¢å¤±è´¥é”™è¯¯ä¿¡æ¯
			alert('æ‚¨ä¼ é€’çš„jsonæ•°æ®æ ¼å¼æœ‰è¯¯ï¼Œè¯·æ ¸å¯¹...');
			alert(error);
			callback(error);
		}
	}
	return _jsonObj;
}
// callbackä¸ºæ•°æ®æ ¼å¼åŒ–é”™è¯¯çš„æ—¶å€™å¤„ç†å‡½æ•°
function formatJson (callback) {
    var jsonObj = $("#input").value;
	// æ­£åˆ™è¡¨è¾¾å¼åŒ¹é…è§„åˆ™å˜é‡
	var reg = null;
	// è½¬æ¢åçš„å­—ç¬¦ä¸²å˜é‡
	var formatted = '';
	// æ¢è¡Œç¼©è¿›ä½æ•°
	var pad = 0;
	// ä¸€ä¸ªtabå¯¹åº”ç©ºæ ¼ä½æ•°
	var PADDING = '  ';
	// jsonå¯¹è±¡è½¬æ¢ä¸ºå­—ç¬¦ä¸²å˜é‡
	var jsonString = transitionJsonToString(jsonObj, callback);
	if (!jsonString) {
		return jsonString;
	}
	// å­˜å‚¨éœ€è¦ç‰¹æ®Šå¤„ç†çš„å­—ç¬¦ä¸²æ®µ
	var _index = [];
	// å­˜å‚¨éœ€è¦ç‰¹æ®Šå¤„ç†çš„â€œå†æ•°ç»„ä¸­çš„å¼€å§‹ä½ç½®å˜é‡ç´¢å¼•
	var _indexStart = null;
	// å­˜å‚¨éœ€è¦ç‰¹æ®Šå¤„ç†çš„â€œå†æ•°ç»„ä¸­çš„ç»“æŸä½ç½®å˜é‡ç´¢å¼•
	var _indexEnd = null;
	// å°†jsonStringå­—ç¬¦ä¸²å†…å®¹é€šè¿‡\r\nç¬¦åˆ†å‰²æˆæ•°ç»„
	var jsonArray = [];
    // æ­£åˆ™åŒ¹é…åˆ°{,}ç¬¦å·åˆ™åœ¨ä¸¤è¾¹æ·»åŠ å›è½¦æ¢è¡Œ
	jsonString = jsonString.replace(/([\{\}])/g, '\r\n$1\r\n');
	// æ­£åˆ™åŒ¹é…åˆ°[,]ç¬¦å·åˆ™åœ¨ä¸¤è¾¹æ·»åŠ å›è½¦æ¢è¡Œ
	jsonString = jsonString.replace(/([\[\]])/g, '\r\n$1\r\n');
	// æ­£åˆ™åŒ¹é…åˆ°,ç¬¦å·åˆ™åœ¨ä¸¤è¾¹æ·»åŠ å›è½¦æ¢è¡Œ
	jsonString = jsonString.replace(/(\,)/g, '$1\r\n');
	// æ­£åˆ™åŒ¹é…åˆ°è¦è¶…è¿‡ä¸€è¡Œçš„æ¢è¡Œéœ€è¦æ”¹ä¸ºä¸€è¡Œ
	jsonString = jsonString.replace(/(\r\n\r\n)/g, '\r\n');
	// æ­£åˆ™åŒ¹é…åˆ°å•ç‹¬å¤„äºä¸€è¡Œçš„,ç¬¦å·æ—¶éœ€è¦å»æ‰æ¢è¡Œï¼Œå°†,ç½®äºåŒè¡Œ
	jsonString = jsonString.replace(/\r\n\,/g, ',');
	// ç‰¹æ®Šå¤„ç†åŒå¼•å·ä¸­çš„å†…å®¹
	jsonArray = jsonString.split('\r\n');
	jsonArray.forEach(function (node, index) {
		// è·å–å½“å‰å­—ç¬¦ä¸²æ®µä¸­"çš„æ•°é‡
		var num = node.match(/\"/g) ? node.match(/\"/g).length : 0;
		// åˆ¤æ–­numæ˜¯å¦ä¸ºå¥‡æ•°æ¥ç¡®å®šæ˜¯å¦éœ€è¦ç‰¹æ®Šå¤„ç†
		if (num % 2 && !_indexStart) {
			_indexStart = index
		}
		if (num % 2 && _indexStart && _indexStart != index) {
			_indexEnd = index
		}
		// å°†éœ€è¦ç‰¹æ®Šå¤„ç†çš„å­—ç¬¦ä¸²æ®µçš„å…¶å®ä½ç½®å’Œç»“æŸä½ç½®ä¿¡æ¯å­˜å…¥ï¼Œå¹¶å¯¹åº”é‡ç½®å¼€å§‹æ—¶å’Œç»“æŸå˜é‡
		if (_indexStart && _indexEnd) {
			_index.push({
				start: _indexStart,
				end: _indexEnd
			})
			_indexStart = null
			_indexEnd = null
		}
	})
	// å¼€å§‹å¤„ç†åŒå¼•å·ä¸­çš„å†…å®¹ï¼Œå°†å¤šä½™çš„"å»é™¤
	_index.reverse().forEach(function (item, index) {
		var newArray = jsonArray.slice(item.start, item.end + 1)
		jsonArray.splice(item.start, item.end + 1 - item.start, newArray.join(''))
	})
	// å¥–å¤„ç†åçš„æ•°ç»„é€šè¿‡\r\nè¿æ¥ç¬¦é‡ç»„ä¸ºå­—ç¬¦ä¸²
	jsonString = jsonArray.join('\r\n');
	// å°†åŒ¹é…åˆ°:åä¸ºå›è½¦æ¢è¡ŒåŠ å¤§æ‹¬å·æ›¿æ¢ä¸ºå†’å·åŠ å¤§æ‹¬å·
	jsonString = jsonString.replace(/\:\r\n\{/g, ':{');
	// å°†åŒ¹é…åˆ°:åä¸ºå›è½¦æ¢è¡ŒåŠ ä¸­æ‹¬å·æ›¿æ¢ä¸ºå†’å·åŠ ä¸­æ‹¬å·
	jsonString = jsonString.replace(/\:\r\n\[/g, ':[');
	// å°†ä¸Šè¿°è½¬æ¢åçš„å­—ç¬¦ä¸²å†æ¬¡ä»¥\r\nåˆ†å‰²æˆæ•°ç»„
	jsonArray = jsonString.split('\r\n');
    // å°†è½¬æ¢å®Œæˆçš„å­—ç¬¦ä¸²æ ¹æ®PADDINGå€¼æ¥ç»„åˆæˆæœ€ç»ˆçš„å½¢æ€
    jsonArray.forEach(function (item, index) {
		console.log(item)
    	var i = 0;
    	// è¡¨ç¤ºç¼©è¿›çš„ä½æ•°ï¼Œä»¥tabä½œä¸ºè®¡æ•°å•ä½
		var indent = 0;
		// è¡¨ç¤ºç¼©è¿›çš„ä½æ•°ï¼Œä»¥ç©ºæ ¼ä½œä¸ºè®¡æ•°å•ä½
		var padding = '';
		if (item.match(/\{$/) || item.match(/\[$/)) {
			// åŒ¹é…åˆ°ä»¥{å’Œ[ç»“å°¾çš„æ—¶å€™indentåŠ 1
			indent += 1
		} else if (item.match(/\}$/) || item.match(/\]$/) || item.match(/\},$/) || item.match(/\],$/)) {
			// åŒ¹é…åˆ°ä»¥}å’Œ]ç»“å°¾çš„æ—¶å€™indentå‡1
			if (pad !== 0) {
				pad -= 1
			}
        } else {
            indent = 0
        }
        for (i = 0; i < pad; i++) {
            padding += PADDING
        }
        formatted += padding + item + '\r\n'
		pad += indent
   	})
	// è¿”å›çš„æ•°æ®éœ€è¦å»é™¤ä¸¤è¾¹çš„ç©ºæ ¼
	$("#input").value = formatted.trim();

}


