	flex: 1;
	display: flex;
	margin-top: -1px;
	border: 1px solid #A9A9A9;
	height: 0;
}
.tabbody>* {
	flex: 1;
	flex-flow: column;
	display: none;
}
.tabbody>.this {
	display: flex;
}
.tabbody>*>.titlebar{
	display: flex;
}
.tabbody>*>.titlebar>*{
	flex: 1;
	margin: 1px 1px 1px 1px;
}
.tabbody>*>.context {
	flex: 1;
	flex-flow: column;
	border: 0;
	padding: 5px;
	overflow-y: auto;
}
