    switch(processedType) {
        case 'novel':
            tab = 3;
            break;
        case 'novelaudio':
            tab = 3;
            break;
        case 'audio':
            tab = 2;
            break;
        case 'comic':
            tab = 8;
            break;
        case 'video':
            tab = 11;
            break;
        default:
            tab = 3;
            break;
    }
}
