            } else {
                isVIP = `${res.vip_level === 1 ? "VIP" : "SVIP"} (æ°¸ä¹…)`;
            }
        }
    } else {
        isVIP = 'æ‚¨å°šæœªå¼€é€šVIP';
    }
    return isVIP;
}

