function extractQueryParams(input) {
  let genreType = '';
  let gender = '';
  const genreTypeMatch = input.match(/[?&]genreType=([^&]+)/);
  if (genreTypeMatch && genreTypeMatch[1]) {
    genreType = decodeURIComponent(genreTypeMatch[1]);
  }
  const genderMatch = input.match(/[?&]gender=([^&]+)/);
  if (genderMatch && genderMatch[1]) {
    gender = decodeURIComponent(genderMatch[1]);
  }
  return [genreType, gender];
}

