var html = result;
var doc = org.jsoup.Jsoup.parse(html);

var lastLink = doc.select("li.page-item a.page-link").last();

if (lastLink) {
    var href = lastLink.attr("href");
    var match = href.match(/index_(\d+)\.html/);
    var maxPage = match ? parseInt(match[1], 10) : 1;

    var baseUrl = href.substring(0, href.lastIndexOf("index_"));

    var list = [];
    for (var i = 2; i <= maxPage; i++) {
        list.push(baseUrl + "index_" + i + ".html");
    }

    list;
} else {
    [];
}
